/*  Contents  */
/*  (1) Universal CSS */
/*  (2) Grid Areas */
/*  (3) Header CSS*/
/*  (4) Body CSS */
/*  (5) Artist Page CSS */
/*  (9) Footer CSS */


/*  (1) Universal CSS START */
* {
  margin: 0px;
  padding: 0px;
  box-sizing: border-box;
  font-family: 'Lato', sans-serif;
}

a {
  text-decoration: none;
  color: inherit;
}

li {
  list-style: none;
}

body {
  background-image: url("https://raw.githubusercontent.com/nate-fritz/nate-fritz.github.io/master/static/media/wood-background.png");
  background-attachment: fixed;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
}

/*  (1) Universal CSS END */
/*  (2) Grid Areas START */
/*  (2) Grid Areas END */
/*  (3) Header CSS START */

.logo {
  height: 70px;
  margin: -10px;
}

.logo2 {
  display: flex;
  margin-top: 110px;
  margin-bottom: 20px;
  justify-content: space-around;
  height: 350px;
  width: 100%;
}

.logo2 > img {
  height: auto;
  width: 350px;
}

header {
  background-color: rgba(0, 0, 15, 0.9); 
  display: flex;
  flex-direction: columns;
  justify-content: space-between;
  padding: 20px;
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 999;
}

.menu__button {
  border: none;
  background-color: transparent;
  color: white;
  cursor: pointer;
  font-size: 30px;
  margin: 10px;
}

.menu__button:hover {
  cursor: pointer;
  transform: scale(1.1);
}

header h1 {
  font-family: 'IM Fell English', serif;
  grid-column-start: 2;
  text-align: center;
  margin-top: 10px;
}

.nav__menu {
  display: none;
  padding-right: 90px;
}

.nav__menu ul,
.nav__menu li{
  font-family: 'IM Fell English', serif;
  font-size: 30px;
  display: flex;
  flex-direction: columns;
  justify-content: space-around;
}

.nav__menu li:hover {
  cursor: pointer;
  text-shadow: 1px 1px 50px white;
}

/*  (3) Header CSS END */

/*  (4) Body CSS START */

h1 {
display: inherit;
}


.logo__banner {
  text-align: center;
}

.main__subtitle {
  margin: 10px;
}

